mapper.select:
select exists (
	select 1 from user 
	where user_id = #{userId}
)

mapper.update:
UPDATE user
SET point = #{point}
WHERE user_id = #{userId}

UPDATE user
password = #{password}, name = #{name}, email = #{email}, address = #{address}, address_detail = #{addressDetail}, phone = #{phone}, gender = #{gender}, birth_date = #{birth},
WHERE user_id = #{userId}
